import React, { Component } from 'react';
import ExperienceCard from './ExperienceCard'
import mthreeLogo from '../images/mthree.png'

class ExperiencePage extends Component {
  state = {
    mthree: {
      logo: mthreeLogo,
      company: "The Software Guild - Mthree",
      position: "Full Stack Java Developer Trainee",
      dates: "July 2020 - Present",
      description: `<div>I attended Mthree’s Aspire Scholarship program and its virtual 
      Full Stack Java Development course. In a matter of 3 months, we attained the 
      skills to build fully functional full stack applications. We learned core 
      concepts such as Agile methodologies, Software Development Life Cycle, MVC 
      Designs, CRUD applications, and ACID compliance.\n\nThe training program 
      eplicated a work environment that consisted of daily stand-ups, weekly 
      assignments for each course module, set hours of 9-5, and working in a 
      consistent Agile workflow.\n\nWe started with understanding Java and building 
      MVC designs, then moving on to database tools such as SQL, JDBC Template, and 
      JPA. We then explored the Spring Framework as well as Spring Boot and the 
      concept of RESTful APIs. Finally, we dabbled on front-end technologies and 
      frameworks, such as HTML5/CSS/JavaScript ES6, Node.js, React.js, Bootstrap, 
      and Thymeleaf. And in the last three weeks, we learned to connect the database, 
      back-end, and front-end together to create full stack applications.</div>`
    }
  }

  render() {
    let { mthree } = this.state

    return (
      <div class="container-fluid" style={{ backgroundColor: "#E2D5C0", flexWrap: "wrap" }}>
        <div class="container">
          <div class="row">
            <div class="col">
              <p className="h2">Experience</p>
            </div>
          </div>
          <div class="row">
            <div className="underscore-dark" />
          </div>
          <div class="row">
            <ExperienceCard
              logo={mthree.logo}
              company={mthree.company}
              position={mthree.position}
              dates={mthree.dates}
              description={mthree.description}
            />
          </div>
        </div>
      </div>
    )
  }
}

export default ExperiencePage;
